data LToken = Comment String    
            | Literal Literal
            | Varid   String
            | Conid   String
            | ReserveID RID
            | Varsym String
            | Consym String
            | ReservedOP ROP
            | Special ST
  deriving (Eq,Show)


data ST = LParen
        | RParen
        | Comma
        | SemiColon
        | LSBracket
        | RSBracket
        | BTick
        | LCurly
        | RCurly
        deriving (Eq,Show)



data ROP = DDot
         | Colon
         | DColon
         | Equal
         | BSlash
         | Pipe
         | LArrow
         | RArrow
         | At
         | Sym
         | DArrow
  deriving (Eq,Show)

data RID = Case
         | Class
         | Data
         | Default
         | Deriving
         | Do
         | Else
         | If
         | Import
         | In
         | Infix
         | Infixl
         | Infixr
         | Instance
         | Let
         | Module
         | Newtype
         | Of
         | Then
         | Type
         | Where
         | Underscore
         deriving (Eq,Show)

createVaridOrReserveID :: String -> LToken
createVaridOrReserveID s = case s of
            "case" -> ReserveID Case
            "class" -> ReserveID Class
            "data" -> ReserveID Data
            "default" -> ReserveID Default
            "deriving" -> ReserveID Deriving
            "do" -> ReserveID Do
            "else" -> ReserveID Else
            "if" -> ReserveID If
            "import" -> ReserveID Import
            "in" -> ReserveID In
            "infix" -> ReserveID Infix
            "infixl" -> ReserveID Infixl
            "infixr" -> ReserveID Infixr
            "instance" -> ReserveID Instance
            "let" -> ReserveID Let
            "module" -> ReserveID Module
            "newtype" -> ReserveID Newtype
            "of" -> ReserveID Of
            "then" -> ReserveID  Then
            "type" -> ReserveID Type
            "where" -> ReserveID Where
            "_" -> ReserveID Underscore
            _      -> Varid     s


mkSpecial :: String -> LToken
mkSpecial s = Special specialToken
  where
    specialToken = case s of
         "("  -> LParen
         ")"  -> RParen
         ","  -> Comma
         ";"  -> SemiColon
         "["  -> LSBracket
         "]"  -> RSBracket
         "`"  -> BTick
         "{"  -> LCurly
         "}"  -> RCurly
         _    -> error "Lexing Error: Not a special token"


createXsymOrReservedOp :: (String -> LToken) -> String -> LToken
createXsymOrReservedOp symBuilder s =
    case tryBuildingReservedOP s of
      Just x -> ReservedOP x
      Nothing -> symBuilder s

tryBuildingReservedOP :: String -> Maybe ROP
tryBuildingReservedOP s = case s of
            ".." -> Just DDot
            ":"  -> Just Colon
            "::"  -> Just DColon
            "="  -> Just Equal
            "\\"  -> Just BSlash
            "|"  -> Just Pipe
            "<-"  -> Just LArrow
            "->"  -> Just RArrow
            "@"  -> Just At
            "~"  -> Just Sym
            "=>"  -> Just DArrow
            _   -> Nothing

mkChar :: String -> LToken
mkChar _ = Literal $ TChar 'a'

mkString :: String -> LToken
mkString  s = Literal $ TString s

mkInteger :: String -> LToken
mkInteger  s = Literal $ TInteger 1

