alex_action_3 =  mkString 
alex_action_4 =  mkInteger 
alex_action_5 =  mkSpecial 
alex_action_6 =  createVaridOrReserveID  
alex_action_7 =  Conid 
alex_action_8 =  createXsymOrReservedOp Varsym  
alex_action_9 =  createXsymOrReservedOp Consym  
